// Buscar planta por ID
export const getPlantById = (id) => {
  return PLANT_CATALOG.find(plant => plant.id === id);
};

// Buscar plantas por categoria
export const getPlantsByCategory = (category) => {
  return PLANT_CATALOG.filter(plant => plant.category === category);
};

// Buscar plantas por nome (busca parcial)
export const searchPlants = (query) => {
  const normalizedQuery = query.toLowerCase().normalize('NFD').replace(/[\u0300-\u036f]/g, '');
  return PLANT_CATALOG.filter(plant => {
    const normalizedName = plant.name.toLowerCase().normalize('NFD').replace(/[\u0300-\u036f]/g, '');
    const normalizedScientific = plant.scientific_name.toLowerCase().normalize('NFD').replace(/[\u0300-\u036f]/g, '');
    return normalizedName.includes(normalizedQuery) || normalizedScientific.includes(normalizedQuery);
  });
};

// Obter todas as categorias com contagem
export const getCategoriesWithCount = () => {
  const counts = {};
  PLANT_CATALOG.forEach(plant => {
    counts[plant.category] = (counts[plant.category] || 0) + 1;
  });
  
  return Object.entries(PLANT_CATEGORIES).map(([key, value]) => ({
    id: key,
    ...value,
    count: counts[key] || 0,
  }));
};

// Obter total de plantas no cat√°logo
export const getTotalPlantsCount = () => {
  return PLANT_CATALOG.length;
};

